import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';

const ProxiesContext = createContext([]);

export const ProxiesProvider = ({ children }) => {
    const [proxies, setProxies] = useState([]);
    const { authToken } = useAuth();

    useEffect(() => {
        if (authToken) {
            fetchProxies();
        }
    }, [authToken]);

    const fetchProxies = async () => {
        try {
            const response = await axios.get('http://localhost:5000/proxies', {
                headers: { Authorization: `Bearer ${authToken}` }
            });
            setProxies(response.data);
        } catch (error) {
            console.error("Error fetching proxies", error);
        }
    };

    const addProxy = async (ip, port) => {
        try {
            await axios.post('http://localhost:5000/proxies', { ip_address: ip, port: port }, {
                headers: { Authorization: `Bearer ${authToken}` }
            });
            fetchProxies();
        } catch (error) {
            console.error("Error adding proxy", error);
        }
    };

    const deleteProxy = async (proxyId) => {
        try {
            await axios.delete(`http://localhost:5000/proxies/${proxyId}`, {
                headers: { Authorization: `Bearer ${authToken}` }
            });
            fetchProxies();
        } catch (error) {
            console.error("Error deleting proxy", error);
        }
    };

    return (
        <ProxiesContext.Provider value={{ proxies, addProxy, deleteProxy }}>
            {children}
        </ProxiesContext.Provider>
    );
};

export const useProxies = () => useContext(ProxiesContext);
