import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Dashboard() {
    const [proxies, setProxies] = useState([]);
    const [newProxyIp, setNewProxyIp] = useState('');
    const [newProxyPort, setNewProxyPort] = useState('');

    useEffect(() => {
        fetchProxies();
    }, []);

    const fetchProxies = async () => {
        try {
            const response = await axios.get('http://localhost:5000/proxies', {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            setProxies(response.data);
        } catch (error) {
            console.error("Error fetching proxies", error);
            alert('Failed to fetch proxies');
        }
    };

    const handleAddProxy = async () => {
        try {
            const response = await axios.post('http://localhost:5000/proxies', {
                ip_address: newProxyIp,
                port: newProxyPort
            }, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            fetchProxies();  // Refresh the list after adding
            setNewProxyIp('');
            setNewProxyPort('');
        } catch (error) {
            console.error("Error adding proxy", error);
            alert('Failed to add proxy');
        }
    };

    const handleDeleteProxy = async (proxyId) => {
        try {
            const response = await axios.delete(`http://localhost:5000/proxies/${proxyId}`, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            fetchProxies();  // Refresh the list after deleting
        } catch (error) {
            console.error("Error deleting proxy", error);
            alert('Failed to delete proxy');
        }
    };

    return (
        <div>
            <h2>My Proxies</h2>
            <div>
                <input
                    type="text"
                    placeholder="IP Address"
                    value={newProxyIp}
                    onChange={(e) => setNewProxyIp(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Port"
                    value={newProxyPort}
                    onChange={(e) => setNewProxyPort(e.target.value)}
                />
                <button onClick={handleAddProxy}>Add Proxy</button>
            </div>
            <ul>
                {proxies.map(proxy => (
                    <li key={proxy.id}>
                        {proxy.ip_address}:{proxy.port} - Status: {proxy.status}
                        <button onClick={() => handleDeleteProxy(proxy.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Dashboard;
